import React,{useState} from 'react';
import './NewExpenses.css'

const NewExpense = (props) =>{
const [etitle ,setTitle] = useState("");
const [eamount,setAmount] = useState("");
const [entereddate , setDate] = useState("");
const [isDisable , setButton] = useState("disbaled");
const handleChangeTitle = (event)=>{

    setTitle(event.target.value);
    if(etitle.length === 0 || eamount.length === 0 || entereddate.length === 0){
       setButton("disabled");
    }
    else{
      setButton("");
    }
    console.log(etitle);
} ;

const handleChangeAmount = (event)=>{

     setAmount(event.target.value);
    //  console.log(eamount);
    console.log(eamount);
    if (
      etitle.length === 0 ||
      eamount.length === 0 ||
      entereddate.length === 0
    ) {
      setButton("disabled");
    } else {
      setButton("");
    }
}

const handleChangeDate = (event) =>{
    setDate(event.target.value);
    console.log(entereddate);
    if (
      etitle.length === 0 ||
      eamount.length === 0 ||
      entereddate.length === 0
    ) {
      setButton("disabled");
    } else {
      setButton("");
    }

}

const handleSubmit = (event) =>{
     event.preventDefault();
   // console.log(event)
     const expenseData = {

         title : etitle,
         amount : eamount ,
         date : new Date(entereddate)
     }
     props.addData(expenseData);
    // console.log(entereddate);
     setDate("");
     setTitle("");
     setAmount("");
}


     return (

         <form  >
        <div className = "new-expense">
           <div className="new-expense__controls">
             <div className="new-expense__control">
               <label>Title</label>
               <input
                 type="text"
                 value = {etitle}
                 onChange = {handleChangeTitle}
               />
             </div>
             <div className="new-expense__control">
               <label>Amount</label>
               <input
                 type="number"
                 min="0.01"
                 step="0.01"
                 value = {eamount}
                 onChange = {handleChangeAmount}
               />
             </div>
             <div className="new-expense__control">
               <label>Date</label>
               <input
                 type="date"
                 min="2019-01-01"
                 max="2022-12-31"
                 value = {entereddate}
                 onChange = {handleChangeDate}
               />
             </div>
           </div>
           <div className="new-expense__actions">
             <button type="submit" {...isDisable} onClick = {handleSubmit}>Add Expense</button>
           </div>
        </div>
         </form>

     );
}

export default NewExpense ;
